mapper.select:
SELECT A .UNID, A .UNAME, A .LNAME, A .LTEL, A .ADDRESS, A .CREATER, A .MODIFYER, A .CREATEDATE, A .MODIFYDATE, A .AREACODE, A . ACCOUNT, A. AREANAME, A.LNG, A.LAT, NVL (B.BOXNUM, 0) AS BOXNUM, NVL (C.WHITENUM, 0) AS WHITENUM, NVL (D .WARNNUM, 0) AS WARNNUM FROM DM_STATION_B A LEFT JOIN ( SELECT COUNT (*) AS BOXNUM, UNID FROM DM_ST_R WHERE 1 = 1 GROUP BY UNID ) B ON A .UNID = B.UNID LEFT JOIN ( SELECT COUNT (*) AS WHITENUM, UNID FROM DM_STF_N WHERE 1 = 1 GROUP BY UNID ) c ON A .UNID = c.UNID LEFT JOIN ( SELECT COUNT (*) AS WARNNUM, UNID FROM DM_STRANGER_H WHERE 1 = 1 GROUP BY UNID ) D ON A .UNID = D .UNID
WHERE 1=1 ${_parameter}
ORDER BY UNID DESC
